import React, { useState } from 'react';
import RunProgramButton from '../components/RunProgramButton';
import './Generate.css';

const Generate = () => {
  const [statusHistory, setStatusHistory] = useState([]);

  // Function to capture status updates from the Run Program button
  const onStatusUpdate = (status, timestamp = new Date()) => {
    setStatusHistory(prev => [
      { status, timestamp, id: Date.now() },
      ...prev
    ].slice(0, 10)); // Keep last 10 statuses
  };

  return (
    <div className="generate-page">
      <div className="generate-header">
        <h1>Generate Foster Bios</h1>
        <p className="description">
          Run the automated program to call fosters, process transcripts, and generate pet biographies.
        </p>
        
        {/* Centered Run Program Button */}
        <div className="button-container">
          <RunProgramButton onStatusUpdate={onStatusUpdate} />
        </div>
      </div>
      
      <div className="status-history">
        <h2>Recent Activity</h2>
        {statusHistory.length === 0 ? (
          <p className="no-activity">No recent activity. Click "Run Program" to start processing.</p>
        ) : (
          <ul className="history-list">
            {statusHistory.map(item => (
              <li key={item.id} className="history-item">
                <span className="timestamp">
                  {item.timestamp.toLocaleTimeString()}
                </span>
                <span className="status-text">{item.status}</span>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Generate;
